import Announcement from "../components/Announcement";
import styled from "styled-components"
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import { Add, Remove } from "@material-ui/icons";

const Container = styled.div``;

const Wrapper = styled.div`
padding:20px;
font-weight: 600;
`;

const Title = styled.h1`
font-weight: 900;
text-align: center;
`;

const Top = styled.div`
display:flex;
align-items: center;
justify-content: space-between;
padding: 20px;
`;

const TopButton = styled.button`
 padding: 10px;
 font-weight 1000;
 cursor: pointer; 
 border: ${(props)=>props.type === "filled" && "none"};
 background-color: ${(props)=>props.type === "filled" ? "black" : "transparent"};
 color: ${(props)=>props.type === "filled" && "white"};
`;

const TopTexts = styled.div``;
const TopText = styled.span`
text-decoration: underline;
cursor: pointer;
margin: 0px 10px;
`;


const Bottom = styled.div`
display:flex;
justify-content: space-between;
`;

const Info = styled.div`
flex:3;
`;


const Hr = styled.hr`
background-color: #eee
border: none;
height:1px;
`

const Product = styled.div`
display:flex;
justify-content: space-between;
`;

const ProductDetail = styled.div`
flex:2;
display: flex;
`;

const ProductAmountContainer = styled.div`
display: flex;
align-items: center;
margin-bottom: 20px;
`;

const ProductAmount = styled.div`
font-size: 24px;
margin:5px;

`;

const ProductPrice = styled.div`
font-size:30px;
font-weight:200;
`;

const Image = styled.img`
width: 200px;
`;

const Details = styled.div`
padding:20px;
display:flex;
flex-direction:column;
justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const ProductColor = styled.div`
width: 20px;
height: 20px;
border-radius:50%;
background-color: ${props=>props.color}
`;

const ProductSize = styled.span``;

const PriceDetail = styled.div`
flex:1;
display:flex;
flex-direction:column;
align-items:center;
justify-content: center;
`;

const Summary = styled.div`
flex:1;
border: 0.5px sild lightgray;
`;

const SummaryTitle = styled.h1``;

const SummaryItem = styled.div``;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const SummaryButton = styled.button``;





const Cart = () => {
  return (
    <Container>
        <Navbar />
        <Announcement />
        <Wrapper>
            <Title>YOUR BAG</Title>
            <Top>
                <TopButton>CONTINUE SHOPPING</TopButton>
                <TopTexts>
                    <TopText>Shopping Bag(2)</TopText>
                    <TopText>Your Wishlist(0)</TopText>
                </TopTexts>
                <TopButton type="filled">CHECKOUT NOW</TopButton>
            </Top>
            <Bottom>
                <Info>
                    <Product>
                        <ProductDetail>
                            <Image src="https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1614188818-TD1MTHU_SHOE_ANGLE_GLOBAL_MENS_TREE_DASHERS_THUNDER_b01b1013-cd8d-48e7-bed9-52db26515dc4.png?crop=1xw:1.00xh;center,top&resize=480%3A%2A"/>
                            <Details>
                                <ProductName><b>Product:</b> NYKEE RUNNING SHOES</ProductName>
                                <ProductId><b>ID:</b> 77654387589</ProductId>
                                <ProductColor color="black"/>
                                <ProductSize><b>Size:</b> 8US</ProductSize>
                            </Details>
                        </ProductDetail>
                        <PriceDetail>
                            <ProductAmountContainer>
                                <Add />
                                <ProductAmount>2</ProductAmount>
                                <Remove />
                            </ProductAmountContainer>
                            <ProductPrice>₹ 3799</ProductPrice>
                        </PriceDetail>
                    </Product>
                    <Hr />
                    <Product>
                        <ProductDetail>
                            <Image src="https://cdn.shopify.com/s/files/1/0623/8850/2700/products/WM0040_700x.png?v=1654782484"/>
                            <Details>
                                <ProductName><b>Product:</b> TimeLapse t-shirt</ProductName>
                                <ProductId><b>ID:</b> 5685749996</ProductId>
                                <ProductColor color="lightblue"/>
                                <ProductSize><b>Size:</b> M</ProductSize>
                            </Details>
                        </ProductDetail>
                        <PriceDetail>
                            <ProductAmountContainer>
                                <Add />
                                <ProductAmount>1</ProductAmount>
                                <Remove />
                            </ProductAmountContainer>
                            <ProductPrice>₹ 699</ProductPrice>
                        </PriceDetail>
                    </Product>
                </Info>
                <Summary>ORDER SUMMARY</Summary>
                <SummaryItem>
                    <SummaryItemText>Subtotal</SummaryItemText>
                    <SummaryItemPrice>₹ 4498</SummaryItemPrice>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText>Estimated Shipping</SummaryItemText>
                    <SummaryItemPrice>₹ 100</SummaryItemPrice>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText>Shipping Discount</SummaryItemText>
                    <SummaryItemPrice>₹ -100</SummaryItemPrice>
                </SummaryItem>
                <SummaryItem>
                    <SummaryItemText type="total">Subtotal</SummaryItemText>
                    <SummaryItemPrice>₹ 4498</SummaryItemPrice>
                </SummaryItem>
                <Button>CHECKOUT NOW</Button>
            </Bottom>
        </Wrapper>
        <Footer />
    </Container>
  )
}

export default Cart
