import { Facebook, Instagram, Mail, Phone, Pinterest, Room, Twitter} from "@material-ui/icons";
import styled from "styled-components"

const Container= styled.div`
display: flex;
`;
const Left= styled.div`
flex: 1;
display: flex;
flex-direction: column;
padding:20px;

`;
const Center= styled.div`
flex: 1;
padding: 20px;
`;


const Right= styled.div`
flex: 1;
padding: 20px;
`;

const ContactItem = styled.div`
margin-bottom:20px;
display: flex;
align-items: center;
`;
const Payment = styled.img``;

const Logo = styled.h1``;
const Desc = styled.p`
margin: 20px 0px;
`;
const SocialContainer = styled.div`
display: flex;
`;
const SocialIcon = styled.div`
 width: 40px;
 height: 40px;
 border-radius: 50px;
 color: white;
 background-color: #${props=>props.color};
 display: flex;
 align-items: center;
 justify-content: center;
 margin-right: 20px;
`;

const Title = styled.h3`
margin-bottom: 30px;
`;
const List = styled.ul`
margin:0;
padding:0;
list-style: none;
display:flex;
flex-wrap: wrap;
`;
const ListItem = styled.li`
width:50%;
margin-bottom: 10px`;



const Footer = () => {
  return (
    <Container>
        <Left>
            <Logo>Just bAIy</Logo>
            <Desc>we offer a wide range of fashionable clothing, shoes, and accessories. From classic basics to trendy statement pieces, we've got you covered. Enjoy fast shipping, easy returns, and excellent customer service. Stay on top of the latest fashion trends with us!</Desc>
            <SocialContainer>
                <SocialIcon color="3B5999">
                    <Facebook />
                </SocialIcon>
                <SocialIcon color="E4405F">
                    <Instagram />
                </SocialIcon>
                <SocialIcon color="55ACEE">
                    <Twitter />
                </SocialIcon>
                <SocialIcon color="E60023">
                    <Pinterest/>
                </SocialIcon>
            </SocialContainer>
        </Left>
        <Center>
            <Title>Useful Links</Title>
            <List>
                <ListItem>Home</ListItem>
                <ListItem>Cart</ListItem>
                <ListItem>Man Fashion</ListItem>
                <ListItem>Woman Fashion</ListItem>
                <ListItem>Accessories</ListItem>
                <ListItem>My Account</ListItem>
                <ListItem>Order Tracking</ListItem>
                <ListItem>Wishlist</ListItem>
                <ListItem>Terms</ListItem>
            </List>
        </Center>
        <Right>
            <Title>Contact</Title>
            <ContactItem><Room/>HSR Layout, Banglore</ContactItem>
            <ContactItem><Phone />+91 408 923 4332</ContactItem>
            <ContactItem><Mail />justbaiy@hotmail.com</ContactItem>
            <Payment src="https://i.ibb.co/Qfvn4z6/payment.png" />
        </Right>
    </Container>
  )
}

export default Footer
